function calculateProduct() {
    // Get input values
    var firstName = document.getElementById('firstName').value;
    var secondNumber = document.getElementById('secondNumber').value;

    // Convert input values to numbers
    var firstNum = parseFloat(firstName);
    var secondNum = parseFloat(secondNumber);

    // Check if the conversion is successful
    if (isNaN(firstNum) || isNaN(secondNum)) {
        document.getElementById('result').innerHTML = 'Please enter valid numbers.';
    } else {
        // Perform the calculation
        var product = multiplyNumbers(firstNum, secondNum);

        // Display the result
        document.getElementById('result').innerHTML = firstName + ' is a text type number, and under that is ' + secondNumber + ' type text number. The product is: ' + product;
    }
}

function multiplyNumbers(a, b) {
    return a * b;
}